import { prisma } from "../../utils/prismaClient";

async function MakeExperience(req, res) {
  console.log(req.body);
  const {
    name,
    company = false,
    personal = false,
    from = "Dec 20",
    to = "Jan 21",
    description,
  } = req.body;
  const data = await prisma.experience.create({
    data: {
      name,
      company,
      personal,
      from,
      to,
      description,
    },
  });
  res.json(data);
}

export default MakeExperience;
