import React, { useState } from "react";
import dynamic from "next/dynamic";
import styled from "styled-components";
import { prisma } from "../utils/prismaClient";
import base from "axios";
const axios = base.create({
  baseURL: "http://localhost:3000/api",
});

const DynamicComponentWithNoSSR = dynamic(
  () => import("../components/PdfView"),
  { ssr: false }
);

const App = styled.div`
  .pdf {
    display: flex;
    justify-content: space-between;
  }
  iframe {
    width: 50vw;
    height: 100vh;
  }
`;

const Index = (props) => {
  console.log("props:", props);

  const [form, setForm] = useState({
    company: "",
    name: "",
    description: "",
  });
  console.log("form:", form);

  function handleTextChange(e) {
    const { name, value } = e.target;
    setForm({ ...form, [name]: value });
  }

  async function handleSubmit(e) {
    e.preventDefault();
    try {
      const newForm = Object.entries(form).reduce((acc, val) => {
        if (!val[1]) {
          return acc;
        }
        return { ...acc, [val[0]]: val[1] };
      }, {});
      const { data } = await axios.post("create", newForm);
      console.log("data:", data);
    } catch (error) {
      console.log("error:", error);
    }
  }
  return (
    <App>
      <h1>Hello</h1>
      <div className="pdf">
        <form onSubmit={handleSubmit}>
          <label>Name of project</label>
          <input value={form.name} name="name" onChange={handleTextChange} />
          <textarea
            name="description"
            value={form.description}
            onChange={handleTextChange}
          />
          <button type="submit">Submit</button>
        </form>
        <DynamicComponentWithNoSSR experiences={props.experiences} />
      </div>
    </App>
  );
};

export default Index;

export async function getStaticProps() {
  const experiences = await prisma.experience.findMany({});

  return { props: { experiences } };
}
